import React from 'react'
import styled from 'styled-components'
import { VscLoading } from 'react-icons/vsc'
import theme from '../../style/theme'
import {
  PreviewContainer,
  ThumbnailContainer,
  DescriptionContainer,
  CategoryBox,
} from './Content.style'

const LoadingDiv = styled(ThumbnailContainer)`
  border: 0.09rem solid ${theme.color.lineGrey};
  display: flex;
  align-items: center;
  justify-content: center;
`

const CategoryDiv = styled(CategoryBox)`
  width: 10rem;
  height: ${theme.size.textXS};
  background-color: ${theme.color.textGrey};
`

const TitleDiv = styled.div`
  width: 50%;
  height: 1.2rem;
  font-size: 1.2rem;
  margin-bottom: 0.1rem;
  background-color: #828282;
`

const TagsDiv = styled.div`
  width: 20%;
  height: 1rem;
  background-color: #828282;
  margin-bottom: 0.4rem;
  display: flex;
  justify-content: flex-start;
  align-items: flex-start;
  :nth-child(n) {
    margin-right: 0.3rem;
  }
  :last-child {
    margin-right: 0rem;
  }
`

const LikeCountDiv = styled.div`
  width: 10%;
  height: 0.8rem;
  background-color: #828282;
  display: flex;
  font-size: 0.8rem;
  color: red;
  align-items: flex-start;
  :first-child {
    margin-right: 0.2rem;
  }
`

function LoadingContent() {
  return (
    <PreviewContainer>
      <LoadingDiv>
        <VscLoading size="2rem" />
      </LoadingDiv>
      <DescriptionContainer>
        <CategoryDiv />
        <TitleDiv />
        <TagsDiv />
        <LikeCountDiv />
      </DescriptionContainer>
    </PreviewContainer>
  )
}

export default LoadingContent
