import React from 'react'
import styled from 'styled-components'
import { VscLoading } from 'react-icons/vsc'
import theme from '../../style/theme'
import { PreviewContainer, ThumbnailContainer } from './Content.style'

const LoadingDiv = styled(ThumbnailContainer)`
  border: 0.09rem solid ${theme.color.lineGrey};
  display: flex;
  align-items: center;
  justify-content: center;
  :first-child {
    display: inline-block;
    position: absolute;
    transform: translate(50%, 50%);
    bottom: 50%;
    right: 50%;
  }
`

const DescriptionDiv = styled.div`
  padding: 0.1rem 0.1rem 0rem 0.1rem;
`

const SubDesDiv = styled.div`
  padding-left: 0.1rem;
  font-size: 0.8rem;
  display: flex;
  justify-content: space-between;
`

const CategoryDiv = styled.div`
  width: 25%;
  background-color: #828282;
  display: flex;
  align-items: center;
`

const TitleDiv = styled.div`
  width: 30%;
  height: 1.2rem;
  font-size: 1.2rem;
  margin-bottom: 0.1rem;
  background-color: #828282;
`

const TagsDiv = styled.div`
  width: 20%;
  height: 1rem;
  background-color: #828282;
  margin-bottom: 0.4rem;
  display: flex;
  justify-content: flex-start;
  align-items: flex-start;
  :nth-child(n) {
    margin-right: 0.3rem;
  }
  :last-child {
    margin-right: 0rem;
  }
`

const LikeCountDiv = styled.div`
  width: 10%;
  height: 0.8rem;
  background-color: #828282;
  display: flex;
  font-size: 0.8rem;
  color: red;
  align-items: flex-start;
  :first-child {
    margin-right: 0.2rem;
  }
`

function LoadingContent() {
  return (
    <PreviewContainer>
      <LoadingDiv>
        <VscLoading size="2rem" />
      </LoadingDiv>
      <DescriptionDiv>
        <SubDesDiv>
          <CategoryDiv />
        </SubDesDiv>
        <TitleDiv />
        <TagsDiv />
        <LikeCountDiv />
      </DescriptionDiv>
    </PreviewContainer>
  )
}

export default LoadingContent
