import React from 'react'
import styled from 'styled-components'
import { VscLoading } from 'react-icons/vsc'
import theme from '../../style/theme'
import {
  PreviewContainer,
  ThumbnailContainer,
  DescriptionContainer,
  CategoryBox,
  TitleBox,
  SubDesContainer,
} from './Content.style'

const LoadingContainer = styled(ThumbnailContainer)`
  border: 0.09rem solid ${theme.color.lineGrey};
  display: flex;
  align-items: center;
  justify-content: center;
`

const CategoryMask = styled(CategoryBox)`
  width: 10rem;
  height: ${theme.size.textXS};
  background-color: ${theme.color.textGrey};
`

const TitleMask = styled(TitleBox)`
  width: 50%;
  height: ${theme.size.titleMD};
  background-color: ${theme.color.textGrey};
`

const TagsMask = styled.div`
  width: 20%;
  height: 1rem;
  background-color: #828282;
  margin-bottom: 0.4rem;
  display: flex;
  justify-content: flex-start;
  align-items: flex-start;
  :nth-child(n) {
    margin-right: 0.3rem;
  }
  :last-child {
    margin-right: 0rem;
  }
`

const LikeCountMask = styled.div`
  width: 10%;
  height: 0.8rem;
  background-color: #828282;
  display: flex;
  font-size: 0.8rem;
  color: red;
  align-items: flex-start;
  :first-child {
    margin-right: 0.2rem;
  }
`

function LoadingContent() {
  return (
    <PreviewContainer>
      <LoadingContainer>
        <VscLoading size="2rem" />
      </LoadingContainer>
      <DescriptionContainer>
        <SubDesContainer>
          <CategoryMask />
        </SubDesContainer>
        <TitleMask />
        <TagsMask />
        <LikeCountMask />
      </DescriptionContainer>
    </PreviewContainer>
  )
}

export default LoadingContent
