import React from 'react'
import styled from 'styled-components'
import { VscLoading } from 'react-icons/vsc'
import theme from '../../style/theme'
import {
  PreviewContainer,
  ThumbnailContainer,
  DescriptionContainer,
  CategoryBox,
  TitleBox,
  SubDesContainer,
  TagsContainer,
} from './Content.style'

const maskBackground = styled.css`
  background-color: ${theme.color.textGrey};
`

const LoadingContainer = styled(ThumbnailContainer)`
  border: 0.09rem solid ${theme.color.lineGrey};
  display: flex;
  align-items: center;
  justify-content: center;
`

const CategoryMaskBox = styled(CategoryBox)`
  ${maskBackground}
  width: 75%;
  height: ${theme.size.textXS};
`

const TitleMaskBox = styled(TitleBox)`
  ${maskBackground}
  width: 75%;
  width: 50%;
  height: ${theme.size.titleMD};
`

const TagsMaskContainer = styled(TagsContainer)`
  ${maskBackground}
  width: 75%;
  width: 30%;
  height: 1rem;
  display: flex;
  justify-content: flex-start;
  align-items: flex-start;
  :nth-child(n) {
    margin-right: 0.3rem;
  }
  :last-child {
    margin-right: 0rem;
  }
`

const LikeMaskBox = styled.div`
  ${maskBackground}
  width: 10%;
  height: 0.8rem;
`

function LoadingContent() {
  return (
    <PreviewContainer>
      <LoadingContainer>
        <VscLoading size="2rem" />
      </LoadingContainer>
      <DescriptionContainer>
        <SubDesContainer>
          <CategoryMaskBox />
        </SubDesContainer>
        <TitleMaskBox />
        <TagsMaskContainer />
        <LikeMaskBox />
      </DescriptionContainer>
    </PreviewContainer>
  )
}

export default LoadingContent
