import React from 'react'
import styled, { css, keyframes } from 'styled-components'
import { CgSpinner } from 'react-icons/cg'
import theme from '../../style/theme'
import {
  PreviewContainer,
  ThumbnailContainer,
  DescriptionContainer,
  CategoryBox,
  TitleBox,
  SubDesContainer,
  TagsContainer,
  LikeBox,
} from './Content.style'

const spin = keyframes`
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
`

const loadingAnimation = keyframes`
  0% {
    background-position: 200% 50%;
  }
  100% {
    background-position: -100% 50%;
  }
`

const maskBackground = css`
  background: linear-gradient(
    90deg,
    ${theme.color.lineGrey} 25%,
    ${theme.color.textGrey} 50%,
    ${theme.color.lineGrey} 75%
  );
  background-size: 300% 300%;
  animation: ${loadingAnimation} 2500ms linear infinite;
`

const LoadingContainer = styled(ThumbnailContainer)`
  border: 0.09rem solid ${theme.color.lineGrey};
  display: flex;
  align-items: center;
  justify-content: center;
`

const LoadingIcon = styled(CgSpinner)`
  color: ${theme.color.textGrey};
  animation: ${spin} 1000ms linear infinite;
`

const CategoryMaskBox = styled(CategoryBox)`
  ${maskBackground}
  width: 80%;
  height: ${theme.size.textXS};
`

const TitleMaskBox = styled(TitleBox)`
  ${maskBackground}
  width: 60%;
  height: ${theme.size.titleMD};
`

const TagsMaskBox = styled(TagsContainer)`
  ${maskBackground}
  width: 40%;
  height: ${theme.size.titleMD};
`

const LikeMaskBox = styled(LikeBox)`
  ${maskBackground}
  width: 20%;
  height: ${theme.size.textXS};
`

function LoadingContent() {
  return (
    <PreviewContainer>
      <LoadingContainer>
        <LoadingIcon size="2rem" />
      </LoadingContainer>
      <DescriptionContainer>
        <SubDesContainer>
          <CategoryMaskBox />
        </SubDesContainer>
        <TitleMaskBox />
        <TagsMaskBox />
        <LikeMaskBox />
      </DescriptionContainer>
    </PreviewContainer>
  )
}

export default LoadingContent
