import React, { useCallback, useEffect, useMemo, useState } from 'react'
import PropTypes from 'prop-types'
import { IconContext } from 'react-icons'
// import axios from 'axios'
import { FaHeart, FaRegHeart } from 'react-icons/fa6'
import theme from '../../style/theme'

function getLikeContent() {}
// productId, userId
function postLikeContent() {}
// productId, userId
function deleteLikeContent() {}
// likeId

function LikeButton({
  productId,
  userId = undefined,
  iconSize = '1.3rem',
  text = null,
}) {
  const [likeId, setLikeId] = useState(undefined)
  const [liked, setLiked] = useState(false)

  useEffect(() => {
    if (userId !== undefined) {
      const tmpId = getLikeContent(productId, userId)
      setLikeId(tmpId)
      setLiked(tmpId !== undefined)
    }
  }, [productId, userId])

  const handleLiked = useCallback(() => {
    if (userId === undefined) {
      alert('로그인이 필요합니다.')
    } else if (!liked) {
      postLikeContent(productId, userId)
      // axios.post('POST API', { product_id: productId, user_id: userId })
      setLiked(true)
    } else {
      deleteLikeContent(likeId)
      // axios.delete(`DELETE API/${likeId}`)
      setLiked(false)
    }
  }, [liked, userId, productId, likeId])

  const iconColor = useMemo(
    () => (liked ? 'red' : theme.color.textGrey),
    [liked]
  )
  const iconStyle = useMemo(
    () => ({ color: `${iconColor}`, size: `${iconSize}` }),
    [iconColor, iconSize]
  )
  const iconAttr = useMemo(
    () => ({ type: 'button', onClick: `${handleLiked}` }),
    [handleLiked]
  )
  const iconValue = useMemo(() => {
    return { style: { iconButtonStyle }, attr: { iconButtonAttr } }
  }, [iconButtonStyle, iconButtonAttr])

  return (
    <IconContext.Provider
      value={{ color: iconColor, size: iconSize, attr: iconButtonAttr }}
    >
      {liked ? <FaHeart /> : <FaRegHeart />}
      {text || null}
    </IconContext.Provider>
  )
}

LikeButton.propTypes = {
  productId: PropTypes.number.isRequired,
  userId: PropTypes.number,
  iconSize: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
  text: PropTypes.string,
}

export default LikeButton
