import React from 'react'
import {
  styled,
  css,
  // keyframes
} from 'styled-components'
import { VscLoading } from 'react-icons/vsc'
import theme from '../../style/theme'
import {
  PreviewContainer,
  ThumbnailContainer,
  DescriptionContainer,
  CategoryBox,
  TitleBox,
  SubDesContainer,
  TagsContainer,
  LikeBox,
} from './Content.style'

const maskBackground = css`
  background-color: ${theme.color.textGrey};
`

// const maskBoxFade = keyframes`
// `

const LoadingContainer = styled(ThumbnailContainer)`
  border: 0.09rem solid ${theme.color.lineGrey};
  display: flex;
  align-items: center;
  justify-content: center;
  :first-child {
    color: ${theme.color.textGrey};
    -webkit-animation: spin 2s infinite;
    @keyframes spin {
      0% {
        transform: {
          rotate(0deg);
        }
      }
      100% {
        transform: {
          rotate(360deg);
        }
      }
    }
  }
`

const CategoryMaskBox = styled(CategoryBox)`
  ${maskBackground}
  width: 80%;
  height: ${theme.size.textXS};
`

const TitleMaskBox = styled(TitleBox)`
  ${maskBackground}
  width: 60%;
  height: ${theme.size.titleMD};
`

const TagsMaskBox = styled(TagsContainer)`
  ${maskBackground}
  width: 40%;
  height: ${theme.size.titleMD};
`

const LikeMaskBox = styled(LikeBox)`
  ${maskBackground}
  width: 20%;
  height: ${theme.size.textXS};
`

function LoadingContent() {
  return (
    <PreviewContainer>
      <LoadingContainer>
        <VscLoading size="2rem" />
      </LoadingContainer>
      <DescriptionContainer>
        <SubDesContainer>
          <CategoryMaskBox />
        </SubDesContainer>
        <TitleMaskBox />
        <TagsMaskBox />
        <LikeMaskBox />
      </DescriptionContainer>
    </PreviewContainer>
  )
}

export default LoadingContent
